#include<bits/stdc++.h>
using namespace std;const int N=1<<18;int n,q,A[N],L[N],R[N],X[N];multiset<int>T[N*2];void apply(int l,int r,int x,int p){for(l+=N,r+=N;l<r;l>>=1,r>>=1){if(l&1){p==1?T[l].insert(x):T[l].erase(T[l].find(x));l++;}if(r&1){--r;p==1?T[r].insert(x):T[r].erase(T[r].find(x));}}}int get(int k,int a){for(k+=N;k;k>>=1)if(!T[k].empty())a=max(a,*T[k].rbegin());return a;}int main(){cin>>n;for(int i=0;i<n;i++)cin>>A[i];cin>>q;for(int t,k=0;k<q;k++){cin>>t;if(t==1){cin>>L[k]>>R[k]>>X[k];L[k]--;apply(L[k],R[k],X[k],1);}if(t==2){int i;cin>>i;i--;apply(L[i],R[i],X[i],-1);}if(t==3){int i;cin>>i;i--;cout<<get(i,A[i])<<'\n';}}}